function redirectToLogin(){$.cookie("cookie_redirect")!==undefined&&window.location.replace($.cookie("cookie_redirect")+window.location.search)}function checkLoginChoice(){var e=$(".js-save-login-choice"),t=$(".js-delete-login-choice");if($.cookie("cookie_redirect")!==undefined){e.removeClass("is-visible");t.toggleClass("is-visible")}else{e.toggleClass("is-visible");t.removeClass("is-visible")}}function showHidePassword(e){if(document.documentMode<9)return;$('input[type="password"]').hideShowPassword({show:!1,innerToggle:!0,toggleClass:"form--toggle-password",states:{shown:{toggleText:e.hide},hidden:{toggleText:e.show}}})}function useOtherSubmitUrl(){if(!$(".js-message").length)return;$("form").submit(function(e){e.preventDefault();var t=$(this).serializeArray(),n=$(this).attr("action").replace("/action/","/=/logon/");$.post(n,t,"json").done(function(e){e.authenticated&&e.redirect&&window.location.replace(e.redirect)}).fail(function(e){$(".js-message").html(e.responseJSON.message).addClass("message--error")})})}$(document).ready(function(){$("form").h5Validate();var e=$.cookie("tidyLanguage")==="en"?"en":"da",t={da:{toogleText:{hide:"Skjul",show:"Vis"},deleteAllCookies:"Cookies blev slettet"},en:{toogleText:{hide:"Hide",show:"Show"},deleteAllCookies:"Cookies deleted"}};showHidePassword(t[e].toogleText);checkLoginChoice();$(".js-save-login-choice").click(function(){$.cookie("cookie_redirect",window.location.pathname,{expires:30,path:"/"});checkLoginChoice();return!1});$(".js-delete-login-choice").click(function(){$.removeCookie("cookie_redirect",{path:"/"});checkLoginChoice();return!1});$(".js-toplink").click(function(){history.back(1);return!1});useOtherSubmitUrl();navigator.javaEnabled()||$(".js-javaenabled").hide()});