/**
 * Cookies
 *
 * Cookie is used for redirecting the user to the last used login
 */// Redirect to login choice.
function redirectToLogin(){$.cookie("cookie_redirect")!=undefined&&window.location.replace($.cookie("cookie_redirect")+window.location.search)}function checkLoginSaved(){var e=$(".js-footer-saved-login");$.cookie("cookie_redirect")!=undefined?e.show():e.hide()}function saveLoginChoice(){$.cookie("cookie_redirect",window.location.pathname,{expires:30,path:"/"});$(".js-cookie-message-saved").text("Dit loginvalg er gemt")}function deleteLoginChoice(){$.removeCookie("cookie_redirect",{path:"/"});$(".js-cookie-message-not-saved").text("Dit loginvalg er slettet")}function deleteAllCookies(e){$.removeCookie("cookie_redirect",{path:"/"});$.removeCookie("cookie_hide_message",{path:"/"});e&&e.text("Cookies blev slettet")}function cookieMessage(){var e=$(".js-cookie-message"),t=$(".js-cookie-message-saved"),n=$(".js-cookie-message-not-saved");if($.cookie("cookie_redirect")!=undefined){t.hide();n.show()}else{t.show();n.hide()}$.cookie("cookie_hide_message")!=1&&e.show();$(".js-hide-message").click(function(){$.cookie("cookie_hide_message",1,{expires:30,path:"/"});e.hide();return!1})}function showHidePassword(){$('input[type="password"]').hideShowPassword({show:!1,innerToggle:!0,toggleClass:"form--toggle-password",states:{shown:{toggleText:"Skjul"},hidden:{toggleText:"Vis"}}})}$(document).ready(function(){showHidePassword();checkLoginSaved();if($(".js-cookie-message")){cookieMessage();$(".js-save-login-choice").click(function(){saveLoginChoice();return!1});$(".js-delete-login-choice").click(function(){deleteLoginChoice();return!1})}$(".js-delete-cookies-link").click(function(){deleteAllCookies($(".js-delete-cookies"));return!1});$(".js-footer-saved-login").click(function(){deleteAllCookies();return!1});$(".js-toplink").click(function(){deleteAllCookies();window.location.replace("/auth/method/"+window.location.search);return!1})});